package operators;

public class ArithmeticOperatorDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(10 + 10);
		System.out.println(10 - 10);
		System.out.println(10 / 10);
		System.out.println(10 * 10);
		System.out.println(10 % 10); // Remainder

	}

}
